
import Image from 'next/image';

type LazyImageProps = {
    src: string;
    alt: string;
    className?: string;
    width?: number;
    height?: number;
  };
  

export const LazyImage = ({ src, alt, className = '', width, height }:LazyImageProps) => {
  return (
    <Image
      src={src}
      alt={alt}
      className={`lazyload ${className}`}
      width={width}
      height={height}
      loading="lazy"
      placeholder="blur"
      blurDataURL="data:image/jpeg;base64,"
    />
  );
};


const CloudinaryImage = ({ src, alt, className = '' }:LazyImageProps) => {
  return (
    <Image
      src={src}
      alt={alt}
      className={className}
      width={800} // Adjust based on your needs
      height={600} // Adjust based on your needs
      loading="lazy"
      placeholder="blur"
      blurDataURL="data:image/jpeg;base64,"
      sizes="(max-width: 768px) 100vw, (max-width: 1200px) 50vw, 33vw"
      quality={75} // Adjust based on your needs
      priority={false} // Set to true if this is above the fold
    />
  );
};

export default CloudinaryImage;